let timers=[];const setTimer=()=>{timers.forEach((e,t)=>{let n=document.createElement("h2");n.innerHTML=t+1+". "+e.title,document.getElementById("timers").appendChild(n);let l=document.createElement("div");const s="timer-"+t;l.id=s,document.getElementById("timers").appendChild(l);const m=new Date(e.time).getTime();setInterval((e=m,t=s)=>{var n=(new Date).getTime(),l=e-n;const i=Math.floor(l/864e5),a=Math.floor(l%864e5/36e5),o=Math.floor(l%36e5/6e4),r=Math.floor(l%6e4/1e3),d=e=>'<span class="time">'+(e<10?"0":"")+e+"</span>";document.getElementById(s).innerHTML=d(i)+"DAYS "+d(a)+"HOURS "+d(o)+"MINUTES "+d(r)+"SECONDS "},1e3)})},deleteEvent=()=>{const e=document.getElementById("delete-id").value;e>1&&e<1e3?(timers.splice(e-1,1),localStorage.setItem("timers",JSON.stringify(timers)),window.location.reload(!0)):alert("Plese enter valid ID")},setNewEvent=()=>{let e={title:document.getElementById("event-name").value,time:document.getElementById("event-time").value};e.time&&e.title?(timers.push(e),localStorage.setItem("timers",JSON.stringify(timers)),window.location.reload(!0)):alert("Please set event title, date and time")};document.getElementById("event-time").value=(new Date).getTime();let userTimers=localStorage.getItem("timers");userTimers&&(timers=JSON.parse(userTimers)),setTimer();const openPanel=()=>{const e=document.getElementsByClassName("panel")[0];e.classList.contains("panel--open")?e.classList.remove("panel--open"):e.classList.add("panel--open")};